
## Decorative transition screens for MACROSS scripts
<#
   Set $1 to 1 for VF-19, 2 for SDF-1, 3 for VF-1J Gerwalk, 4 for Gubaba, 5 for Minmay,
   6 for Mylene, 7 for Basara, 8 for Hikaru Ichijo, or null for VF-1S

   If these transitions annoy you, just remove the function call "transitionSplash"
   from any script. If you delete this file, you must also delete its "if" statement
   in MACROSS.ps1, otherwise the console will not load!
#>
function transitionSplash($1,$2){
    cls
    if($1 -eq 1){
    $b = ''
    $c = "                                           MACROSS 7 VF-19 'FIRE'"
    }
    elseif($1 -eq 2){
    $b = ''
    $c = "                                           SDF MACROSS"
    }
    elseif($1 -eq 3){
    $b = ''
    $c = "                                VF-1S VALKRYIE FIGHTER (MACROSS)"
    }
    elseif($1 -eq 4){
    $b = ''
    $c = "                                           Gubaba (MACROSS 7)"
    }
    elseif($1 -eq 5){
    $b = ''
    $c = "                                         LYNN MINMAY (MACROSS DYRL)"
    }
    elseif($1 -eq 6){
    $b = ''
    $c = "                                      MYLENE JENIUS (MACROSS 7)"
    }
    elseif($1 -eq 7){
    $b = ''
    $c = "                                      NEKKI BASARA (MACROSS 7)"
    }
    elseif($1 -eq 8){
    $b = ''
    $c = "                                      HIKARU ICHIJO (MACROSS)"
    }
    else{
    $b = ''
    $c = "                                           DYRL VF-1S 'SKULL LEADER'"
    }

    getThis $b
    Write-Host -f YELLOW "$vf19_READ
    
    ===================================================================================================="
    
    if( $2 -eq 1 ){
        Write-Host "$c"
    }
    else{
        Write-Host "$c"
        slp 2
        cls
    }
}

